// ‚îÄ‚îÄ‚îÄ ROOM PLANNING WIDGET - MIT MASTER-DESIGN ‚îÄ‚îÄ‚îÄ

import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";
import * as XLSX from "https://esm.sh/xlsx@0.18.5";

// ‚îÄ‚îÄ‚îÄ ERWEITERTE FARBPALETTE - 80 EINZIGARTIGE FARBEN ‚îÄ‚îÄ‚îÄ
const MODERN_TASK_COLORS = [
// Rot-Familie (10 Farben)
'#FF6B6B', '#FF4757', '#FF3838', '#FF6347', '#E74C3C', '#C0392B', '#E55039', '#FA8072', '#DC143C', '#B22222',
// Rosa-Familie (10 Farben)
'#FF69B4', '#FFB6C1', '#FFC0CB', '#FF1493', '#DA70D6', '#BA55D3', '#DDA0DD', '#EE82EE', '#FF20FF', '#C71585',
// Orange-Familie (10 Farben)
'#FFB347', '#FF7F50', '#FF8C00', '#FFA500', '#EB984E', '#D2691E', '#F39C12', '#E67E22', '#CD853F', '#FF4500',
// Gelb-Familie (10 Farben)
'#FFEAA7', '#F7DC6F', '#F4D03F', '#F1C40F', '#FFD700', '#FFFF99', '#F9E79F', '#FAD7A0', '#FFFACD', '#FFFFE0',
// Gr√ºn-Familie (10 Farben)
'#96CEB4', '#82E0AA', '#A9DFBF', '#52C41A', '#2ECC71', '#27AE60', '#98D8C8', '#40E0D0', '#00FA9A', '#32CD32',
// T√ºrkis-Familie (10 Farben)
'#4ECDC4', '#A3E4D7', '#00CED1', '#1ABC9C', '#20B2AA', '#48CAE4', '#00B4D8', '#0077B6', '#023E8A', '#03045E',
// Blau-Familie (10 Farben)
'#45B7D1', '#85C1E9', '#AED6F1', '#3498DB', '#2980B9', '#87CEEB', '#4682B4', '#6495ED', '#4169E1', '#0000FF',
// Lila-Familie (10 Farben)
'#BB8FCE', '#D7BDE2', '#9B59B6', '#8E44AD', '#663399', '#9370DB', '#8A2BE2', '#7B68EE', '#6A5ACD', '#483D8B'];

// ‚îÄ‚îÄ‚îÄ KONFIGURATION ‚îÄ‚îÄ‚îÄ
const CONFIG = {
  days: ["Mo", "Di", "Mi", "Do", "Fr"],
  startHour: 7,
  endHour: 18,
  slotMinutes: 15,
  maxPollingAttempts: 25,
  pollingInterval: 200
};

// ‚îÄ‚îÄ‚îÄ HILFSFUNKTIONEN ‚îÄ‚îÄ‚îÄ
function getModernTaskColor(taskName) {
  let hash = 0;
  for (let i = 0; i < taskName.length; i++) {
    hash = taskName.charCodeAt(i) + ((hash << 5) - hash);
  }
  const index = Math.abs(hash) % MODERN_TASK_COLORS.length;
  return MODERN_TASK_COLORS[index];
}
function generateTimeSlots() {
  const slots = [];
  for (let h = CONFIG.startHour; h < CONFIG.endHour; h++) {
    for (let m = 0; m < 60; m += CONFIG.slotMinutes) {
      const hh = String(h).padStart(2, "0");
      const mm = String(m).padStart(2, "0");
      slots.push(`${hh}:${mm}`);
    }
  }
  return slots;
}
function hasUnsavedChanges(gridState) {
  const unsavedCells = Object.values(gridState).filter(cell => cell && cell.root && !cell.existing);
  console.log("üîç hasUnsavedChanges Debug:");
  console.log("  Alle root Zellen:", Object.values(gridState).filter(cell => cell && cell.root));
  console.log("  Ungespeicherte Zellen:", unsavedCells);
  console.log("  Ergebnis:", unsavedCells.length > 0);
  return unsavedCells.length > 0;
}
function calculateEndTime(startTime, durationMinutes) {
  const [hours, minutes] = startTime.split(':').map(Number);
  const startMinutes = hours * 60 + minutes;
  const endMinutes = startMinutes + durationMinutes;
  const endHours = Math.floor(endMinutes / 60);
  const endMins = endMinutes % 60;
  return `${String(endHours).padStart(2, '0')}:${String(endMins).padStart(2, '0')}`;
}

// ‚îÄ‚îÄ‚îÄ DROPDOWN KOMPONENTE ‚îÄ‚îÄ‚îÄ
function RoomPlanningDropdown({
  tasks,
  employees = [],
  onSelect,
  onCancel,
  currentTask = null,
  currentEmployee = null,
  isInTableCell = false,
  loading = false
}) {
  const [searchTerm, setSearchTerm] = React.useState('');
  const [selectedEmployee, setSelectedEmployee] = React.useState(currentEmployee || '');
  const [dropdownPosition, setDropdownPosition] = React.useState({
    top: 0,
    left: 0,
    width: 200,
    maxHeight: 450
  });
  const [portalContainer, setPortalContainer] = React.useState(null);
  const inputRef = React.useRef(null);
  const dropdownRef = React.useRef(null);
  React.useEffect(() => {
    if (!inputRef.current) return;
    inputRef.current.focus();

    // Portal Container erstellen
    const container = document.createElement('div');
    container.style.position = 'absolute';
    container.style.top = '0';
    container.style.left = '0';
    container.style.zIndex = '10000';
    document.body.appendChild(container);
    setPortalContainer(container);

    // Position berechnen
    const rect = inputRef.current.getBoundingClientRect();
    const viewportHeight = window.innerHeight;
    const spaceBelow = viewportHeight - rect.bottom;
    const maxDropdownHeight = Math.max(200, spaceBelow - 20);
    setDropdownPosition({
      top: rect.bottom + window.scrollY + 4,
      left: rect.left + window.scrollX,
      width: Math.max(rect.width, 350),
