      const endTime = calculateEndTime(time, durationMinutes);
      addDebugLog(`Verf√ºgbarkeitspr√ºfung: ${day} ${time}-${endTime} f√ºr Mitarbeiter ${employeeId}`);
      const requestId = `check_${employeeId}_${day}_${time}_${Date.now()}`;

      // Store initialisieren
      appsmith.store.availabilityConflicts = [];
