    }

    .modern-btn {
        padding: 12px 16px;
    }

    .dropdown-list {
        left: 8px !important;
        right: 8px;
        width: calc(100vw - 16px) !important;
        min-width: unset !important;
    }
}

/* ‚îÄ‚îÄ‚îÄ Touch-Ger√§te Optimierungen ‚îÄ‚îÄ‚îÄ */
@media (hover: none) and (pointer: coarse) {
    .task-cell:hover,
    .grid-table td:hover,
    .empty-cell:hover {
        transform: none;
        background: inherit !important;
    }

    .modern-btn:hover {
        transform: none;
    }

    .dropdown-item:hover {
        transform: none;
    }

    .task-name:hover {
        transform: none;
        background: rgba(255, 255, 255, 0.2);
    }

    .dropdown-list {
        max-height: 60vh;
        border-radius: 12px;
    }

    .dropdown-item {
        border-bottom: 1px solid #f1f3f4;
    }
}

/* ‚îÄ‚îÄ‚îÄ Scrollbar Styling ‚îÄ‚îÄ‚îÄ */
.dropdown-list::-webkit-scrollbar {
    width: 6px;
}

.dropdown-list::-webkit-scrollbar-track {
    background: #f8f9fa;
}

.dropdown-list::-webkit-scrollbar-thumb {
    background: #dee2e6;
    border-radius: 3px;
}

.dropdown-list::-webkit-scrollbar-thumb:hover {
    background: #adb5bd;
}

