// ‚îÄ‚îÄ‚îÄ HAUPTKOMPONENTE ‚îÄ‚îÄ‚îÄ
function App(props) {
  // State
  const roomId = appsmith.model?.selectedRoom ?? null;
  const roomName = appsmith.model?.selectedRoomName ?? null;
  const tasks = appsmith.model?.tasks || [];
  const employees = appsmith.model?.employees || [];

  const [allGridStates, setAllGridStates] = React.useState({});
  const [activeCell, setActiveCell] = React.useState(null);
  const [dragSource, setDragSource] = React.useState(null);
  const [debugLog, setDebugLog] = React.useState([]);

  // Debug-Funktion
  const addDebugLog = React.useCallback((message) => {
    console.log("DEBUG:", message);
    setDebugLog(prev => [...prev.slice(-4), `${new Date().toLocaleTimeString()}: ${message}`]);
  }, []);

  // Lade Raum-Schedule
  React.useEffect(() => {
    if (!roomId) {
      setAllGridStates({});
      return;
    }

    const rawData = appsmith.model.initialData || [];
    const loaded = {};

    if (Array.isArray(rawData)) {
      addDebugLog(`Transformiere ${rawData.length} DB-Records zu Widget-Format`);

      rawData.forEach(record => {
        const {
          schedule_id,
          day_abbrev,
          start_time,
          end_time,
          task_name,
          duration_minutes,
          employee_id
        } = record;

        const startTime = start_time.slice(0, 5);
        const key = `${day_abbrev}-${startTime}`;
        const rowspan = Math.ceil(duration_minutes / 15);

        loaded[key] = {
          task: task_name,
          taskId: record.task_id,
          root: true,
          rowspan: rowspan,
          existing: true,
          scheduleId: schedule_id,
          assignedEmployee: employee_id,
          duration_minutes: duration_minutes
        };

        // F√ºlle weitere Zellen f√ºr rowspan
        const slots = generateTimeSlots();
        const startIdx = slots.indexOf(startTime);
        for (let i = 1; i < rowspan; i++) {
          const nextTime = slots[startIdx + i];
          if (nextTime) {
            loaded[`${day_abbrev}-${nextTime}`] = {
              task: task_name,
              taskId: record.task_id,
              root: false,
              rowspan: rowspan,
              existing: true,
              scheduleId: schedule_id,
              assignedEmployee: employee_id,
              duration_minutes: duration_minutes
            };
          }
        }
      });

      addDebugLog(`‚úì Transformiert zu Widget-Keys: [${Object.keys(loaded).join(', ')}]`);
    }

    setAllGridStates(prev => {
      const copy = { ...prev };
      const currentState = copy[roomId] || {};
      const hasUnsavedLocal = hasUnsavedChanges(currentState);

      addDebugLog(`RaumID: ${roomId}, hasUnsavedLocal: ${hasUnsavedLocal}`);

      if (!hasUnsavedLocal) {
        addDebugLog(`‚úì √úbernehme transformierte DB-Daten f√ºr Raum ${roomId} (${Object.keys(loaded).length} Zellen)`);
        copy[roomId] = loaded;
        return copy;
      }

      addDebugLog(`‚ö†Ô∏è Behalte lokale √Ñnderungen f√ºr Raum ${roomId} (${Object.keys(currentState).length} Eintr√§ge)`);
      return prev;
    });
  }, [roomId, JSON.stringify(appsmith.model.initialData || []), addDebugLog]);

  const gridState = allGridStates[roomId] || {};
  const hasChanges = hasUnsavedChanges(gridState);

  // ‚îÄ‚îÄ‚îÄ VERF√úGBARKEITSPR√úFUNG ‚îÄ‚îÄ‚îÄ
  const checkEmployeeAvailability = async (employeeId, day, time, durationMinutes) => {
    if (!employeeId) {
      addDebugLog("‚ùå Kein Mitarbeiter ausgew√§hlt - √ºberspringe Verf√ºgbarkeitspr√ºfung");
      return { available: true, conflicts: [] };
    }

    addDebugLog(`üîç Pr√ºfe Verf√ºgbarkeit f√ºr Mitarbeiter ${employeeId} am ${day} ${time} (${durationMinutes}min)`);

    try {
      const endTime = calculateEndTime(time, durationMinutes);
      addDebugLog(`Verf√ºgbarkeitspr√ºfung: ${day} ${time}-${endTime} f√ºr Mitarbeiter ${employeeId}`);

      const requestId = `check_${employeeId}_${day}_${time}_${Date.now()}`;

      // Store initialisieren
      appsmith.store.availabilityConflicts = [];
      appsmith.store.availabilityRequestId = null;

      // Model aktualisieren
      appsmith.updateModel({
        checkAvailability: {
          employeeId: parseInt(employeeId, 10),
          dayAbbrev: day,
          startTime: time,
          endTime: endTime,
          requestId: requestId
        }
      });

      addDebugLog(`Model aktualisiert mit RequestID: ${requestId}`);

      // Event ausl√∂sen
      await appsmith.triggerEvent("onCheckAvailability");
      addDebugLog("Event ausgel√∂st, warte auf Ergebnis...");

      // Polling f√ºr Ergebnis
      let attempts = 0;

      while (attempts < CONFIG.maxPollingAttempts) {
        await new Promise(resolve => setTimeout(resolve, CONFIG.pollingInterval));
        attempts++;

        const currentRequestId = appsmith.store?.availabilityRequestId;
        const conflicts = appsmith.store?.availabilityConflicts;

        if (currentRequestId === requestId && Array.isArray(conflicts)) {
          addDebugLog(`‚úÖ Verf√ºgbarkeitspr√ºfung abgeschlossen nach ${attempts * CONFIG.pollingInterval}ms: ${conflicts.length} Konflikte`);

          return {
            available: conflicts.length === 0,
            conflicts: conflicts
          };
        }

        addDebugLog(`Polling Attempt ${attempts}/${CONFIG.maxPollingAttempts} - RequestID: ${currentRequestId}, Conflicts: ${conflicts?.length || 'undefined'}`);
      }

      // Timeout erreicht
      addDebugLog(`‚ö†Ô∏è Verf√ºgbarkeitspr√ºfung Timeout nach ${CONFIG.maxPollingAttempts * CONFIG.pollingInterval}ms - verwende Fallback`);
      return { available: true, conflicts: [] };

    } catch (error) {
      addDebugLog(`Verf√ºgbarkeitspr√ºfung fehlgeschlagen: ${error.message} - Task wird trotzdem erstellt`);
      return { available: true, conflicts: [] };
    }
  };

  // ‚îÄ‚îÄ‚îÄ TASK SELECTION ‚îÄ‚îÄ‚îÄ
  const handleTaskSelect = async (selectedTaskId, assignedEmployeeId, cellDay, cellTime) => {
    addDebugLog(`üéØ Task Selection: ${selectedTaskId}, Employee: ${assignedEmployeeId}, Position: ${cellDay}-${cellTime}`);

    if (!roomId || !cellDay || !cellTime) {
      addDebugLog("‚ùå Abbruch: Fehlende Parameter");
      setActiveCell(null);
      return;
    }

    const key = `${cellDay}-${cellTime}`;
    const cellData = gridState[key] || null;

    // Entfernen
    if (selectedTaskId === "") {
      addDebugLog("üóëÔ∏è Task entfernen gew√§hlt");
      if (cellData?.root) {
        if (cellData.existing && cellData.scheduleId) {
          appsmith.updateModel({ scheduleIdToDelete: cellData.scheduleId });
          appsmith.triggerEvent("onDeleteSingle");
        }
        removeBlockLocal(cellDay, cellTime);
      }
      setActiveCell(null);
      return;
    }

    // Task finden
    const taskObj = tasks.find(t => t.id === parseInt(selectedTaskId, 10));
    if (!taskObj) {
      addDebugLog(`‚ùå Task nicht gefunden: ${selectedTaskId}`);
      setActiveCell(null);
      return;
    }

    addDebugLog(`‚úÖ Task gefunden: ${taskObj.name} (${taskObj.duration_minutes}min)`);

    // Verf√ºgbarkeitspr√ºfung
    if (assignedEmployeeId) {
      addDebugLog("üîç STARTE VERF√úGBARKEITSPR√úFUNG");

      try {
        // Loading-Anzeige
        const originalActiveCell = activeCell;
        setActiveCell({ 
          ...originalActiveCell, 
          loading: true 
        });

        const availability = await checkEmployeeAvailability(
          assignedEmployeeId, 
          cellDay, 
          cellTime, 
          taskObj.duration_minutes
        );

        // Loading entfernen
        setActiveCell(originalActiveCell);

        if (!availability.available && availability.conflicts.length > 0) {
          const employeeName = employees.find(e => e.id == assignedEmployeeId)?.name || `Mitarbeiter ${assignedEmployeeId}`;

          const conflictMsg = availability.conflicts.map(c => {
            return `${c.start_time.slice(0,5)}-${c.end_time.slice(0,5)}: ${c.task_name}${c.room_name ? ` (${c.room_name})` : ''}`;
          }).join('\n');

          const confirmed = window.confirm(
            `‚ö†Ô∏è MITARBEITER-KONFLIKT!\n\n${employeeName} ist bereits verplant:\n\n${conflictMsg}\n\nTrotzdem den Termin erstellen/√§ndern?`
          );

          if (!confirmed) {
            addDebugLog("‚ùå Task-Erstellung vom User abgebrochen wegen Konflikt");
            setActiveCell(null);
            return;
          } else {
            addDebugLog("‚úÖ User best√§tigt Erstellung trotz Konflikt");
          }
        } else {
          addDebugLog(`‚úÖ Mitarbeiter ist verf√ºgbar - keine Konflikte gefunden`);
        }

      } catch (error) {
        addDebugLog(`‚ùå Verf√ºgbarkeitspr√ºfung Fehler: ${error.message} - erstelle Task trotzdem`);
        setActiveCell(activeCell);
      }
    } else {
      addDebugLog("‚ÑπÔ∏è Kein Mitarbeiter ausgew√§hlt - √ºberspringe Verf√ºgbarkeitspr√ºfung");
    }

    // Task erstellen/updaten
    try {
      if (cellData?.root && cellData.existing) {
        addDebugLog("üîÑ Update existierenden Task");

        updateSingleTask({
          scheduleId: cellData.scheduleId,
          newTaskId: parseInt(selectedTaskId, 10),
          newDay: cellDay,
          newTime: cellTime,
          assignedEmployee: assignedEmployeeId
        });

        updateBlockInGrid(taskObj, cellDay, cellTime, assignedEmployeeId, cellData.scheduleId, true);
      } else {
        addDebugLog("üÜï Erstelle neuen Task");
        if (cellData?.root && !cellData.existing) {
          removeBlockLocal(cellDay, cellTime);
        }

        createNewBlock(taskObj, cellDay, cellTime, assignedEmployeeId);
        addDebugLog(`‚úÖ Neuer Block erstellt f√ºr Task: ${taskObj.name}`);
      }
    } catch (error) {
      addDebugLog(`‚ùå Fehler beim Task erstellen: ${error.message}`);
    }

    setActiveCell(null);
  };

  // ‚îÄ‚îÄ‚îÄ BLOCK MANAGEMENT ‚îÄ‚îÄ‚îÄ
  const removeBlockLocal = (day, time) => {
    addDebugLog(`üóëÔ∏è Entferne Block: ${day}-${time}`);
    setAllGridStates(prev => {
      const copy = { ...prev };
      const current = { ...(copy[roomId] || {}) };
      const key = `${day}-${time}`;
      const cellData = current[key];

      if (cellData?.root) {
        const rowspan = cellData.rowspan || 1;
        const slots = generateTimeSlots();
        const startIdx = slots.indexOf(time);

        for (let i = 0; i < rowspan; i++) {
          const t = slots[startIdx + i];
          if (!t) break;
          delete current[`${day}-${t}`];
        }
      }

      copy[roomId] = current;
      return copy;
    });
  };

  const createNewBlock = (taskObj, day, time, assignedEmployeeId = null) => {
    addDebugLog(`üÜï Erstelle neuen Block: ${taskObj.name} bei ${day}-${time}`);
    const slots = generateTimeSlots();
    const startIdx = slots.indexOf(time);
    const durationSlots = Math.ceil(taskObj.duration_minutes / CONFIG.slotMinutes);

    setAllGridStates(prev => {
      const copy = { ...prev };
      const current = { ...(copy[roomId] || {}) };

      for (let i = 0; i < durationSlots; i++) {
        const t = slots[startIdx + i];
        if (!t) break;

        current[`${day}-${t}`] = {
          task: taskObj.name,
          taskId: taskObj.id,
          root: i === 0,
          rowspan: durationSlots,
          existing: false,
          scheduleId: null,
          assignedEmployee: assignedEmployeeId,
          duration_minutes: taskObj.duration_minutes
        };
      }

      copy[roomId] = current;
      addDebugLog(`‚úÖ Block erstellt: ${Object.keys(current).length} Zellen im Grid`);
      return copy;
    });
  };

  const updateBlockInGrid = (taskObj, day, time, assignedEmployeeId, scheduleId, existing) => {
    const slots = generateTimeSlots();
    const startIdx = slots.indexOf(time);
    const durationSlots = Math.ceil(taskObj.duration_minutes / CONFIG.slotMinutes);

    setAllGridStates(prev => {
      const copy = { ...prev };
      const current = { ...(copy[roomId] || {}) };

      // Entferne alte Zellen mit derselben scheduleId
      Object.keys(current).forEach(key => {
        if (current[key]?.scheduleId === scheduleId) {
          delete current[key];
        }
      });

      // Erstelle neue Zellen
      for (let i = 0; i < durationSlots; i++) {
        const t = slots[startIdx + i];
        if (!t) break;

        current[`${day}-${t}`] = {
          task: taskObj.name,
          taskId: taskObj.id,
          root: i === 0,
          rowspan: durationSlots,
          existing: existing,
          scheduleId: scheduleId,
          assignedEmployee: assignedEmployeeId,
          duration_minutes: taskObj.duration_minutes
        };
      }

      copy[roomId] = current;
      return copy;
    });
  };

  // ‚îÄ‚îÄ‚îÄ EVENT HANDLERS ‚îÄ‚îÄ‚îÄ
  const updateSingleTask = ({ scheduleId, newTaskId, newDay, newTime, assignedEmployee }) => {
    addDebugLog(`üîÑ updateSingleTask: scheduleId=${scheduleId}, taskId=${newTaskId}, employee=${assignedEmployee}`);

    appsmith.updateModel({
      updateSingleData: {
        scheduleId,
        newTaskId,
        newDay,
        newTime,
        assignedEmployee,
        roomId
      }
    });

    appsmith.triggerEvent("onUpdateSingle");
  };

  const deleteSingleTask = scheduleId => {
    if (!scheduleId || !roomId) return;
    addDebugLog(`üóëÔ∏è L√∂sche Task mit scheduleId: ${scheduleId}`);
    appsmith.updateModel({ scheduleIdToDelete: scheduleId });
    appsmith.triggerEvent("onDeleteSingle");

    setAllGridStates(prev => {
      const copy = { ...prev };
      const current = { ...(copy[roomId] || {}) };
      const rootKey = Object.keys(current).find(
        key => current[key]?.scheduleId === scheduleId && current[key]?.root
      );
      if (rootKey) {
        const [day, time] = rootKey.split("-");
        removeBlockLocal(day, time);
      }
      copy[roomId] = current;
      return copy;
    });
    setActiveCell(null);
  };

  const saveRoom = () => {
    if (!roomId) return;

    const newEntries = {};
    Object.entries(gridState).forEach(([key, cellData]) => {
      if (!cellData.root) return;
      if (cellData.existing) return;
      newEntries[key] = cellData;
    });

    addDebugLog(`üíæ Speichere ${Object.keys(newEntries).length} neue Eintr√§ge`);

    if (Object.keys(newEntries).length === 0) {
      addDebugLog("‚ÑπÔ∏è Keine neuen Eintr√§ge zum Speichern");
      window.alert("Keine neuen Termine zum Speichern vorhanden.");
      return;
    }

    const payload = {
      roomId,
      gridState: JSON.parse(JSON.stringify(newEntries)),
      fullGridState: JSON.parse(JSON.stringify(gridState))
    };

    appsmith.updateModel({ lastSaveData: payload });

    try {
      appsmith.triggerEvent("onSaveRoom");
      addDebugLog("‚úÖ Event erfolgreich ausgel√∂st");

      setAllGridStates(prev => {
        const copy = { ...prev };
        const current = { ...(copy[roomId] || {}) };

        Object.keys(newEntries).forEach(key => {
          if (current[key]) {
            current[key] = {
              ...current[key],
              existing: true
            };
          }
        });

        copy[roomId] = current;
        return copy;
      });

    } catch (error) {
      addDebugLog(`‚ùå Event-Fehler: ${error.message}`);
    }
  };

  const resetRoom = () => {
    if (!roomId) return;
    addDebugLog("üîÑ Raum zur√ºcksetzen - nur lokale ungespeicherte √Ñnderungen");

    setAllGridStates(prev => {
      const copy = { ...prev };
      const current = { ...(copy[roomId] || {}) };

      let removedCount = 0;
      const keysToRemove = [];

      Object.entries(current).forEach(([key, cellData]) => {
        if (cellData && !cellData.existing) {
          keysToRemove.push(key);
          if (cellData.root) removedCount++;
        }
      });

      keysToRemove.forEach(key => {
        delete current[key];
      });

      addDebugLog(`‚úÖ ${removedCount} ungespeicherte Tasks entfernt, DB-Tasks bleiben erhalten`);

      copy[roomId] = current;
      return copy;
    });
  };

  const deleteRoomData = async () => {
    if (!roomId) {
      window.alert("Bitte zuerst einen Raum ausw√§hlen.");
      return;
    }
    addDebugLog("üóëÔ∏è Alle Raum-Daten l√∂schen");
    appsmith.updateModel({ initialData: {} });
    appsmith.triggerEvent("onDeleteAllRoom");
    setAllGridStates(prev => {
      const copy = { ...prev };
      copy[roomId] = {};
      return copy;
    });
    setActiveCell(null);
  };

  const prepareTableData = () => {
    if (!roomId) return [];

    const currentGrid = allGridStates[roomId] || {};
    const timeSlots = generateTimeSlots();
    const tableData = [];

    tableData.push({
      Zeit: `RAUM: ${roomName || `Raum ${roomId}`} (ID: ${roomId})`,
      Mo: "", Di: "", Mi: "", Do: "", Fr: ""
    });

    tableData.push({ Zeit: "", Mo: "", Di: "", Mi: "", Do: "", Fr: "" });

    tableData.push({
      Zeit: "ZEIT", Mo: "MONTAG", Di: "DIENSTAG", 
      Mi: "MITTWOCH", Do: "DONNERSTAG", Fr: "FREITAG"
    });

    for (let time of timeSlots) {
      const row = { Zeit: time };
      for (let day of CONFIG.days) {
        const key = `${day}-${time}`;
        const cell = currentGrid[key];
        if (cell && cell.root) {
          let cellContent = cell.task;
          if (cell.assignedEmployee) {
            const emp = employees.find(e => e.id == cell.assignedEmployee);
            const empName = emp?.name || `MA${cell.assignedEmployee}`;
            cellContent += ` [${empName}]`;
          }
          row[day] = cellContent;
        } else {
          row[day] = "";
        }
      }
      tableData.push(row);
    }

    return tableData;
  };

  React.useEffect(() => {
    const tableData = prepareTableData();
    const entityName = roomName || `Raum_${roomId}`;
    const cleanName = entityName.replace(/[^a-zA-Z0-9√§√∂√º√Ñ√ñ√ú√ü]/g, '_');

    appsmith.updateModel({ 
      tableData: tableData,
      hasTableData: tableData.length > 0,
      roomId: roomId,
      exportFileName: `Raumplan_${cleanName}_${roomId}_${new Date().toISOString().split('T')[0]}`,
      currentRoomName: entityName
    });
  }, [allGridStates, roomId, roomName]);

  const openExportModal = () => {
    if (!roomId) {
      window.alert("Bitte zuerst einen Raum ausw√§hlen, um zu exportieren.");
      return;
    }

    const tableData = prepareTableData();
    if (tableData.length <= 3) {
      window.alert("Raum-Grid ist leer. Nichts zum Exportieren.");
      return;
    }

    appsmith.updateModel({ 
      tableData: tableData,
      exportFileName: `Raumplan_${roomName}_${roomId}_${new Date().toISOString().split('T')[0]}`,
      currentRoomName: roomName
    });

    appsmith.triggerEvent("onOpenExportModal");
  };

  const onDragStart = (e, day, time) => {
    e.dataTransfer.setData("text/plain", JSON.stringify({ day, time }));
    setDragSource({ day, time });
  };

  const onDragOver = e => e.preventDefault();

  const onDrop = (e, targetDay, targetTime) => {
    e.preventDefault();
    setDragSource(null);
  };

  // ‚îÄ‚îÄ‚îÄ MEMOIZED CALCULATIONS ‚îÄ‚îÄ‚îÄ
  const skipCells = React.useMemo(() => {
    const result = {};
    const timeSlots = generateTimeSlots();
    Object.entries(gridState).forEach(([key, cellData]) => {
      if (cellData.root) {
        const [day, time] = key.split("-");
        const idx = timeSlots.indexOf(time);
        for (let i = 1; i < cellData.rowspan; i++) {
          const next = timeSlots[idx + i];
          if (next) {
            result[`${day}-${next}`] = true;
          }
        }
      }
    });
    return result;
  }, [gridState]);

  const handleCellClick = React.useCallback((day, time) => {
    if (!roomId) {
      window.alert("Bitte zuerst einen Raum ausw√§hlen.");
      return;
    }
    addDebugLog(`üéØ Zelle ${day}-${time} angeklickt`);
    setActiveCell({ day, time });
  }, [roomId, addDebugLog]);

  // ‚îÄ‚îÄ‚îÄ RENDER GRID ‚îÄ‚îÄ‚îÄ
  const renderGrid = () => {
    const timeSlots = generateTimeSlots();

    return (
      <div className="widget-container">
        {/* Header */}
        <div className="room-header">
          <h3>üè¢ Raumplanung - Verf√ºgbarkeitspr√ºfung</h3>
          <div className="room-info">
            <strong>Aktueller Raum:</strong> {roomName || 'Kein Raum ausgew√§hlt'} 
            {roomId && ` (ID: ${roomId})`}
          </div>
          <div className="room-details">
            üîç Automatische Verf√ºgbarkeitspr√ºfung bei Mitarbeiter-Zuweisung
          </div>
          <div className="room-details">
            üìä Grid-Status: {Object.keys(gridState).length} Zellen aktiv
          </div>
        </div>

        {/* Ungespeicherte √Ñnderungen */}
        {hasChanges && (
          <div className="unsaved-warning">
            <span>‚ö†Ô∏è</span>
            <span>Du hast ungespeicherte √Ñnderungen!</span>
          </div>
        )}

        {/* Button-Zeile */}
        <div className="button-row">
          <button className="modern-btn btn-reset" onClick={resetRoom}>
            <span>üîÑ</span>
            Zur√ºcksetzen (Raum)
          </button>
          <button className="modern-btn btn-delete-all" onClick={deleteRoomData}>
            <span>üóëÔ∏è</span>
            Alle Termine l√∂schen
          </button>
          <button className="modern-btn btn-save" onClick={saveRoom}>
            <span>üíæ</span>
            Speichern {hasChanges && '(!)'}
          </button>
          <button className="modern-btn btn-export" onClick={openExportModal}>
            <span>üìä</span>
            Excel/CSV Export
          </button>
        </div>

        <table className="grid-table">
          <thead>
            <tr>
              <th className="time-cell">‚è∞ Zeit</th>
              {CONFIG.days.map(day => (
                <th key={day} className="day-cell">
                  üìÖ {day}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {timeSlots.map((time, rowIdx) => (
              <tr key={time}>
                <td className="time-cell">{time}</td>
                {CONFIG.days.map(day => {
                  const key = `${day}-${time}`;
                  if (skipCells[key]) return null;

                  const cellData = gridState[key];
                  const isActive = activeCell?.day === day && activeCell?.time === time;

                  // Task-Zelle
                  if (cellData?.root) {
                    return (
                      <td
                        key={key}
                        rowSpan={cellData.rowspan}
                        className="task-cell"
                        style={{ backgroundColor: getModernTaskColor(cellData.task) }}
                        draggable="true"
                        onDragStart={e => onDragStart(e, day, time)}
                        onDragOver={onDragOver}
                      >
                        {/* Ungespeicherte √Ñnderung Badge */}
                        {!cellData.existing && (
                          <div className="unsaved-badge"></div>
                        )}

                        <div className="task-content">
                          {/* Dropdown oder Task-Anzeige */}
                          {isActive ? (
                            <RoomPlanningDropdown
                              tasks={tasks}
                              employees={employees}
                              currentTask={cellData.task}
                              currentEmployee={cellData.assignedEmployee}
                              onSelect={(taskId, employeeId) => handleTaskSelect(taskId, employeeId, day, time)}
                              onCancel={() => setActiveCell(null)}
                              isInTableCell={true}
                              loading={activeCell?.loading || false}
                            />
                          ) : (
                            <div style={{ display: 'flex', flexDirection: 'column', gap: '4px' }}>
                              <span
                                className="task-name"
                                onClick={() => handleCellClick(day, time)}
                                style={{ color: '#333' }}
                              >
                                {cellData.task}
                              </span>
                              {cellData.assignedEmployee && (
                                <div className="task-employee-badge">
                                  üë§ {employees.find(e => e.id == cellData.assignedEmployee)?.name || `MA${cellData.assignedEmployee}`}
                                </div>
                              )}
                            </div>
                          )}

                          {/* L√∂sch-Button */}
                          {cellData.existing && cellData.scheduleId && (
                            <button
                              className="delete-icon"
                              onClick={() => deleteSingleTask(cellData.scheduleId)}
                              title="Diesen Task l√∂schen"
                            >
                              üóëÔ∏è
                            </button>
                          )}
                        </div>
                      </td>
                    );
                  }

                  // Freie Zelle
                  return (
                    <td
                      key={key}
                      className="empty-cell"
                      onClick={() => handleCellClick(day, time)}
                      onDragOver={onDragOver}
                      onDrop={e => onDrop(e, day, time)}
                    >
                      {isActive ? (
                        <RoomPlanningDropdown
                          tasks={tasks}
                          employees={employees}
                          onSelect={(taskId, employeeId) => handleTaskSelect(taskId, employeeId, day, time)}
                          onCancel={() => setActiveCell(null)}
                          isInTableCell={true}
                          loading={activeCell?.loading || false}
                        />
                      ) : null}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return renderGrid();
}

function render() {
  ReactDOM.render(<App />, document.getElementById("root"));
}

appsmith.onReady(() => {
  render();
});

appsmith.onModelChange(() => {
  render();
});
